mapper.select:
select no, title, station_name, member_id, content, hits,
to_char(posted_time,'YYYY/MM/DD HH24:MI') as posted_time 
from BURN_BOARD where no = #{value}

mapper.update:
update burn_board
set	title = #{title}, content = #{content}, station_name = #{stationName}
where no=#{no}

mapper.select:
select * 
from (select row_number() over(order by no desc) rnum, no, title, station_name, member_id, posted_time, hits 
	  from (select * from BURN_BOARD where station_name = #{stationName})) 
where rnum between #{pagingBean.startRowNumber} and #{pagingBean.endRowNumber}
