mapper.update:
        update member set
		password=#{password}, name=#{name}, sex=#{sex}, tel=#{tel}
		where id=#{id}

        update station set
		simple_detail=#{simpleDetail}, detail=#{detail}, section=#{section}, img=#{img}, lat=#{lat}, lng=#{lng}
		where name=#{name}

        update place set
		name=#{name}, station_name=#{station_name}, area=#{area}
		where no=#{no}
